package com.satyam.day29.inheritance;

public  class Company {
	private String companyName;
	private String registedOfficeLocation;
	public Company() {
		// TODO Auto-generated constructor stub
	}
	public String getCompanyName() {
		return companyName;
	}
	public void setCompanyName(String companyName) {
		this.companyName = companyName;
	}
	public String getRegistedOfficeLocation() {
		return registedOfficeLocation;
	}
	public void setRegistedOfficeLocation(String registedOfficeLocation) {
		this.registedOfficeLocation = registedOfficeLocation;
	}

}
